/**
 * Makes given value available via window at given path in DEV_MODE
 * @param path {string}
 * @param value {*}
 */
export const expose = (path, value) => {
  if (DEV_MODE) {
    const keys = path.split('.');
    let prev = window;
    for (let i = 0; i < keys.length - 1; i += 1) {
      const key = keys[i];
      prev[key] = prev[key] ?? {};
      prev = prev[key];
    }
    prev[keys[keys.length - 1]] = value;
  }
};
