import { serializeQuery } from 'url-helpers/query';
import { serializePath } from 'url-helpers/path';

/**
 * Create a url string with given route and data
 * @param {string} routestring
 * @param {{query: Object, path: Object}} opts
 */
export function serializeURL(routestring, opts = {}) {
  const { path = {}, query = {} } = opts;
  return serializePath(routestring, path) + serializeQuery(query);
}
